window.onload=function(){for(var e=document.querySelectorAll("[data-hover]"),t=0;t<e.length;t++)e[t].onmouseover=function(){var e=this.getAttribute("data-hover");this.classList.add("hovered");for(var t=document.querySelectorAll(e),o=0;o<t.length;o++)t[o].classList.add("target"),t[o].classList.add("hovered")},e[t].onmouseout=function(){var e=this.getAttribute("data-hover");this.classList.remove("hovered");for(var t=document.querySelectorAll(e),o=0;o<t.length;o++)t[o].classList.remove("target"),t[o].classList.remove("hovered")}};