document.addEventListener("DOMContentLoaded",function(e){for(var t=document.querySelectorAll("[data-hover]"),o=0;o<t.length;o++)t[o].onmouseover=function(){var e=this.getAttribute("data-hover");this.classList.add("hovered");for(var t=document.querySelectorAll(e),o=0;o<t.length;o++)t[o].classList.add("target-hovered")},t[o].onmouseout=function(){var e=this.getAttribute("data-hover");this.classList.remove("hovered");for(var t=document.querySelectorAll(e),o=0;o<t.length;o++)t[o].classList.remove("target-hovered")}});