for(var hoverables=document.querySelectorAll("[data-hover]"),i=0;i<hoverables.length;i++)hoverables[i].onmouseover=function(){var e=this.getAttribute("data-hover");this.classList.add("hovered");for(var t=document.querySelectorAll(e),r=0;r<t.length;r++)t[r].classList.add("target-hovered")},hoverables[i].onmouseout=function(){var e=this.getAttribute("data-hover");this.classList.remove("hovered");for(var t=document.querySelectorAll(e),r=0;r<t.length;r++)t[r].classList.remove("target-hovered")};