var hovered={init:function(){window.onload=function(){for(var e=document.querySelectorAll("[data-hover]"),t=0;t<e.length;t++){var o=e[t],r=o.getAttribute("data-hover").trim();o.onmouseenter=function(){o.classList.add('hovered');for(var e=document.querySelectorAll(r),t=0;t<e.length;t++)e[t].classList.add("target-hovered")},o.onmouseout=function(){o.classList.remove('hovered');for(var e=document.querySelectorAll(r),t=0;t<e.length;t++)e[t].classList.remove("target-hovered")}}}}};